import logo from './logo.svg';
import './App.css';
import { MyAPI } from './Hooks/MainAPI';
import { Children, useState } from 'react';
import { ChildAPI } from './Hooks/ChildAPI';

function App() {

	const [selectedItem,setselectedItem] = useState('');

	const mydataAPI = MyAPI({
		filter: {
			id: 0
		}, children: ['results']
	});

	function changeItem(e) {
		setselectedItem(e.target.id);

		const childdataAPI = ChildAPI({
			filter: {
				id: e.target.id
			}, children: ['']
		});
	}


	return (
		<div className="wrapper">

			<header className="App-header">
				<h1>List Of Charactors</h1>
				{mydataAPI.map((row, i) => {
					return <h2 className='itemLink' onClick={changeItem} id={i} key={i}>{i + 1} - {row.name}</h2>
				})}
			</header>
			<div className="App-body">
				<h1>{selectedItem}</h1>
			</div>

		</div>
	);
}

export default App;
